import React from "react";
import ProjectCard from "./ProjectCard";
import { Col, Container, Row, Tab, Nav } from "react-bootstrap";
import colorSharp2 from "../library/img/color-sharp3.jpg";
import TrackVisibility from "react-on-screen";
import "animate.css";
import projectsData from "./projectsData";
import "./Projects.css";

const Project = () => {
  const chunkSize = 3; // Number of projects per row
  const chunkArray = (arr, size) => {
    const chunkedArray = [];
    for (let i = 0; i < arr.length; i += size) {
      chunkedArray.push(arr.slice(i, i + size));
    }
    return chunkedArray;
  };

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>Projects</h2>
                  <p>
                    Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the
                    industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type
                    and scrambled it to make a type specimen book.
                  </p>
                  <Tab.Container id="projects-tabs" defaultActiveKey="first">
                    <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                      {projectsData.map((project, index) => (
                        <Nav.Item key={index}>
                          <Nav.Link eventKey={`tab-${index + 1}`}>Tab {index + 1}</Nav.Link>
                        </Nav.Item>
                      ))}
                    </Nav>
                    <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                      {projectsData.map((project, index) => (
                        <Tab.Pane key={index} eventKey={`tab-${index + 1}`}>
                          {chunkArray(project, chunkSize).map((chunk, chunkIndex) => (
                            <Row key={chunkIndex}>
                              {chunk.map((projectChunk, projectIndex) => (
                                <Col key={projectIndex} sm={12} md={4}>
                                  <ProjectCard {...projectChunk} />
                                </Col>
                              ))}
                            </Row>
                          ))}
                        </Tab.Pane>
                      ))}
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background" />
    </section>
  );
};

// Function to chunk the array into smaller arrays

export default Project;
