import { useState, useEffect, isValidElement } from "react"
import { Container, Row, Col } from "react-bootstrap"
import { ArrowRightCircle } from "react-bootstrap-icons"
import headerImg from '../library/img/header-img.svg'
import 'anime.css'
import TrackVisibility from "react-on-screen"


export const Banner = () =>{
    const [loopNum, setLoopNum] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const toRototate = ["Software Enginner"];
    const [text, setText] = useState('');
    const period = 2000;
    const [delta, setDelta] = useState(300 - Math.random() * 100);

    useEffect(() =>{
        let ticker = setInterval(() =>{
            tick();
        }, delta)

        return () => {clearInterval(ticker)};
    }, [text])

    const tick = () => {
        let i = loopNum % toRototate.length;
        let fullText = toRototate[i];
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1)

        setText(updatedText);

        if(isDeleting) {
            setDelta( prevDelta => prevDelta / 2)
        }

        if(!isDeleting && updatedText === fullText) {
            setIsDeleting(true);
            setDelta(period);
        }else  if(isDeleting && updatedText === '') {
            setIsDeleting(false);
            setLoopNum(loopNum + 1);
            setDelta(500);
        }

    }
    return (
        <section className="banner" id="home">
            <Container>
                <Row className="align-items-center">
                    <Col xs={12} md={6} xl={7}>
                        <TrackVisibility>
                            <div> 
                                <span className="tagline"> Welcome to my Portfolio</span>
                                <h1>{`Hi, I'm Wildy Pierre Jerome`} <span className="wrap"> 
                                {text}
                                </span></h1>
                                <p>
                                    Write a paragrah about my self.
                                </p>
                                <button onClick={() =>console.log('connecct')}> Let's connect <ArrowRightCircle size={25}/> </button>
                            </div>
                        </TrackVisibility>
                    </Col>

                    <Col xs={12} md={6} xl={5}>
                        <img src={headerImg} alt="Headder Img" />
                    </Col >
                </Row>
            </Container>
        </section>
    )
}
